import { Component } from '@angular/core';
import { Cliente } from '../modelo/Cliente';
import { ClientecService } from '../servico/clientec.service';

@Component({
  selector: 'app-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css']
})
export class PrincipalComponent {
   // Variável para visibilidade dos botões
   btnCadastro:boolean = true;

   //JSON de clientes
   clientes:Cliente[] = [];

   // Construtor
   constructor(private servico:ClientecService){

   }

   // Método de selção
   selecionar():void{
    this.servico.selecionar().subscribe(retorno => this.clientes = retorno);
   }

   // Método de inicialização
   ngOnInit(){
    this.selecionar();
   }

}
<form>
    <img src="assets/cliente.png">

    <input type="text" placeholder="Nome" class="form-control">
    <input type="number" placeholder="Idade" class="form-control">
    <input type="text" placeholder="Cidade" class="form-control">

    <input type="button" value="Cadastrar"  *ngIf="btnCadastro"  class="btn btn-primary">
    <input type="button" value="Alterar"   *ngIf="!btnCadastro"  class="btn btn-warning">
    <input type="button" value="Remover"  *ngIf="!btnCadastro"  class="btn btn-danger">
    <input type="button" value="Cancelar"  *ngIf="!btnCadastro" class="btn btn-secondary">
</form>

<table class="table">
    <thead>
        <tr>
            <th>#</th>
            <th>Nome</th>
            <th>Idade</th>
            <th>Cidade</th>
            <th>Selecionar</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let c of clientes">
            <td></td>
            <td>{{c.nome}}</td>
            <td>{{c.idade}}</td>
            <td>{{c.cidade}}</td>
            <td></td>
        </tr>
    </tbody>
</table>
